import { useState } from "react";
import "./login.css";

export default function SignUp() {
  const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
    location: "", // will be auto-filled
  });
  const [err, setErr] = useState({});
  const [loading, setLoading] = useState(false);

  // regex validation
  const usernameRe = /^[A-Za-z]{8,16}$/;
  const emailRe = /^\S+@\S+\.\S+$/;
  const passwordRe = /^(?=.*[A-Z])(?=.*[@!?])[A-Za-z0-9@!?]{8,16}$/;

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const validate = () => {
    const e = {};
    if (!usernameRe.test(form.username)) {
      e.username = "Username must be 8–16 letters (A–Z only).";
    }
    if (!emailRe.test(form.email)) {
      e.email = "Enter a valid email address.";
    }
    if (!passwordRe.test(form.password)) {
      e.password =
        "Password 8–16 chars, ≥1 uppercase, ≥1 special (@, !, ?).";
    }
    if (!form.location) {
      e.location = "Location is required.";
    }
    setErr(e);
    return Object.keys(e).length === 0;
  };

  const onSubmit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    setLoading(true);
    try {
      const res = await fetch("/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Registration failed");
      window.location.href = "/login";
    } catch (e) {
      setErr({ api: e.message });
    } finally {
      setLoading(false);
    }
  };

  // get location from browser + reverse geocode
  const getLocation = () => {
  if (!navigator.geolocation) {
    setErr({ location: "Geolocation not supported." });
    return;
  }
  navigator.geolocation.getCurrentPosition(
    async (pos) => {
      const { latitude, longitude } = pos.coords;
      try {
        const res = await fetch(
          `https://nominatim.openstreetmap.org/reverse?lat=${latitude}&lon=${longitude}&format=json`
        );
        const data = await res.json();
        const city =
          data.address.city ||
          data.address.town ||
          data.address.village ||
          data.address.state;
        setForm((f) => ({
          ...f,
          location: city || "Unknown",
          lat: latitude,
          lon: longitude,
        }));
      } catch (e) {
        setErr({ location: "Failed to fetch city." });
      }
    },
    (error) => {
      setErr({ location: "Permission denied or unavailable." });
    }
  );
};


  return (
    <div className="auth-card">
      <div className="brand-icon" aria-hidden>🛒</div>
      <h2 className="title">Create Account ✨</h2>
      <p className="subtitle">Join us to start saving on your shopping list.</p>

      {err.api && <div className="error" role="alert">{err.api}</div>}

      <form onSubmit={onSubmit} noValidate>
        {/* Username */}
        <div className="field">
          <label className="label" htmlFor="username">Username</label>
          <input
            id="username"
            name="username"
            className="input"
            placeholder="Enter a username"
            value={form.username}
            onChange={onChange}
            aria-invalid={!!err.username}
          />
          {err.username && <div className="error">{err.username}</div>}
        </div>

        {/* Email */}
        <div className="field">
          <label className="label" htmlFor="email">Email</label>
          <input
            id="email"
            name="email"
            type="email"
            className="input"
            placeholder="you@example.com"
            value={form.email}
            onChange={onChange}
            aria-invalid={!!err.email}
          />
          {err.email && <div className="error">{err.email}</div>}
        </div>

        {/* Password */}
        <div className="field">
          <label className="label" htmlFor="pw">Password</label>
          <input
            id="pw"
            name="password"
            type="password"
            className="input"
            placeholder="Choose a strong password"
            value={form.password}
            onChange={onChange}
            aria-invalid={!!err.password}
          />
          {err.password && <div className="error">{err.password}</div>}
        </div>

      {/* Location */}
      <div className="field">
        <label className="label">Location</label>
        <div style={{ display: "flex", gap: "8px" }}>
          <input
            className="input"
            name="location"
            placeholder="Enter your city or click 📍"
            value={form.location}
            onChange={onChange}
            aria-invalid={!!err.location}
          />
          <button
            type="button"
            onClick={getLocation}
            style={{ padding: "0 12px", cursor: "pointer" }}
            title="Use my current location"
          >
            📍
          </button>
        </div>
        {err.location && <div className="error">{err.location}</div>}

        {/* Show map if we have coordinates */}
        {form.lat && form.lon && (
          <iframe
            title="map"
            width="100%"
            height="200"
            style={{ marginTop: "12px", borderRadius: "8px", border: "1px solid #ccc" }}
            src={`https://www.google.com/maps?q=${form.lat},${form.lon}&hl=es;z=14&output=embed`}
          />
        )}
      </div>


        <button className="primary" disabled={loading}>
          {loading ? "Creating account..." : "Sign Up"}
        </button>
      </form>

      <div className="split">
        <a href="/login">Already have an account? Login</a>
      </div>
    </div>
  );
}
