// src/pages/SignUp.jsx
import { useState } from "react";
import "./login.css";
import Button from "../../../components/Button";
import AuthCard from "../../../components/AuthCard";
import FormField from "../../../components/FormField";
import GoogleButton from "../../../components/GoogleButton";
import PasswordInput from "../../../components/PasswordInput";
import LocationInput from "../components/LocationInput"; // we'll add this next

export default function SignUp() {
  const [form, setForm] = useState({
    username: "",
    email: "",
    password: "",
    location: "",
    lat: null,
    lon: null,
  });
  const [err, setErr] = useState({});
  const [loading, setLoading] = useState(false);

  const usernameRe = /^[A-Za-z]{8,16}$/;
  const emailRe = /^\S+@\S+\.\S+$/;
  const passwordRe = /^(?=.*[A-Z])(?=.*[@!?])[A-Za-z0-9@!?]{8,16}$/;

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const validate = () => {
    const e = {};
    if (!usernameRe.test(form.username)) {
      e.username = "Username must be 8â€“16 letters (Aâ€“Z only).";
    }
    if (!emailRe.test(form.email)) {
      e.email = "Enter a valid email address.";
    }
    if (!passwordRe.test(form.password)) {
      e.password = "Password 8â€“16 chars, â‰¥1 uppercase, â‰¥1 special (@, !, ?).";
    }
    if (!form.location) {
      e.location = "Location is required.";
    }
    setErr(e);
    return Object.keys(e).length === 0;
  };

  const onSubmit = async (ev) => {
    ev.preventDefault();
    if (!validate()) return;
    setLoading(true);
    try {
      const res = await fetch("/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Registration failed");
      window.location.href = "/login";
    } catch (e) {
      setErr({ api: e.message });
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthCard
      title="Create Account âœ¨"
      subtitle="Join us to start saving on your shopping list."
      icon="ðŸ›’"
    >
      {err.api && <div className="error" role="alert">{err.api}</div>}

      <form onSubmit={onSubmit} noValidate>
        {/* Username */}
        <FormField id="username" label="Username" error={err.username}>
          <input
            id="username"
            name="username"
            placeholder="Enter a username"
            value={form.username}
            onChange={onChange}
            aria-invalid={!!err.username}
          />
        </FormField>

        {/* Email */}
        <FormField id="email" label="Email" error={err.email}>
          <input
            id="email"
            name="email"
            type="email"
            placeholder="you@example.com"
            value={form.email}
            onChange={onChange}
            aria-invalid={!!err.email}
          />
        </FormField>

        {/* Password */}
        <PasswordInput
          id="pw"
          label="Password"
          value={form.password}
          onChange={(e) => setForm((f) => ({ ...f, password: e.target.value }))}
          error={err.password}
        />

        {/* Location */}
        <LocationInput
          value={form.location}
          lat={form.lat}
          lon={form.lon}
          onChange={(val) => setForm((f) => ({ ...f, ...val }))}
          error={err.location}
        />

        <Button variant="primary" fullWidth type="submit" loading={loading}>
          Sign Up
        </Button>
      </form>

      <GoogleButton onClick={() => (window.location.href = "/api/auth/google")} />

      <div className="split">
        <a href="/login">Already have an account? Login</a>
      </div>

      <p className="footer-note">Cheapest Shopping List Near Me</p>
    </AuthCard>
  );
}
