import React, { useState } from "react";
import "./PasswordInput.css";

/**
 * PasswordInput
 * Props:
 *  - id (string)            required for label/aria
 *  - name (string)          e.g. "password"
 *  - value (string)
 *  - onChange (fn)          (e) => void
 *  - placeholder (string)
 *  - error (string)         (optional) if you're also passing error into FormField, this can be omitted
 *  - autoComplete (string)  e.g. "current-password" | "new-password"
 *  - required, disabled, onBlur, className  (optional passthrough)
 */
export default function PasswordInput({
  id = "password",
  name = "password",
  value = "",
  onChange,
  placeholder = "Enter your password",
  error,
  autoComplete = "current-password",
  required,
  disabled,
  onBlur,
  className = "",
}) {
  const [show, setShow] = useState(false);

  return (
    <div className={`password-input ${className}`}>
      <input
        id={id}
        name={name}
        type={show ? "text" : "password"}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        autoComplete={autoComplete}
        required={required}
        disabled={disabled}
        onBlur={onBlur}
        aria-invalid={!!error}
      />
      <button
        type="button"
        className="password-toggle"
        onClick={() => setShow((v) => !v)}
        aria-label={show ? "Hide password" : "Show password"}
        aria-pressed={show}
        title={show ? "Hide password" : "Show password"}
      >
        {show ? "üôà" : "üëÅÔ∏è"}
      </button>
      {/* Optional inline error if you prefer it here instead of FormField */}
      {error ? <div className="password-error">{error}</div> : null}
    </div>
  );
}
