import { useState } from "react";
import "./login.css"; 


export default function SignUp() {
  const [form, setForm] = useState({
    email: "",
    username: "",
    password: "",
  });
  const [err, setErr] = useState({});
  const [loading, setLoading] = useState(false);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const onSubmit = async (ev) => {
    ev.preventDefault();
    setLoading(true);
    try {
      const res = await fetch("/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Registration failed");
      window.location.href = "/login";
    } catch (e) {
      setErr({ api: e.message });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-card">
      <div className="brand-icon" aria-hidden>ðŸ›’</div>
      <h2 className="title">Create Account âœ¨</h2>
      <p className="subtitle">Join us to start saving on your shopping list.</p>

      {err.api && <div className="error" role="alert">{err.api}</div>}

      <form onSubmit={onSubmit} noValidate>
        <div className="field">
          <label className="label" htmlFor="email">Email</label>
          <input
            id="email"
            name="email"
            type="email"
            className="input"
            placeholder="you@example.com"
            value={form.email}
            onChange={onChange}
          />
        </div>

        <div className="field">
          <label className="label" htmlFor="username">Username</label>
          <input
            id="username"
            name="username"
            className="input"
            placeholder="Choose a username"
            value={form.username}
            onChange={onChange}
          />
        </div>

        <div className="field">
          <label className="label" htmlFor="pw">Password</label>
          <input
            id="pw"
            name="password"
            type="password"
            className="input"
            placeholder="Choose a strong password"
            value={form.password}
            onChange={onChange}
          />
        </div>

        <button className="primary" disabled={loading}>
          {loading ? "Creating account..." : "Sign Up"}
        </button>
      </form>

      <div className="split">
        <a href="/login">Already have an account? Login</a>
      </div>
    </div>
  );
}
